sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
 ms.include("IllumioMIDConstants");
    var parameters = JSON.parse(inputs.parameters);
    var timeZone = parameters["time_zone"];
    var pceUrl = parameters["pce_url"];
    var pceEndpoint = parameters["pce_endpoint"];
    var pceAuthorization = parameters["pce_authorization"];
    var orgId = parameters["org_id"];
    
    var utils = new IllumioPCEUtils(timeZone);
    var protocol = utils.getPortFromUrl(pceUrl);

    var decodedAuth = utils.decodeBase64(pceAuthorization);
    decodedAuth = decodedAuth.split(":");
    var pceUsername = decodedAuth[0];
    var pcePassword = decodedAuth.slice(1).join(':');
    var pceMIDProxy = parameters["enable_pce_mid_proxy"];
    var retryParams = DEFAULT_RETRY_PARAMS;

    var pceHttpClient = new IllumioHTTPClient(pceUrl, pceUsername,pcePassword, protocol, pceMIDProxy, retryParams);
    var pceEndpoint = "/api/v2/orgs/"+orgId+"/sec_policy/active/label_groups";

    var queryString = encodeURI(inputs.queryString);
    var response = pceHttpClient.get(pceEndpoint,queryString, "");

    outputs.status = response.status;
    outputs.response = JSON.stringify(response.data);
    outputs.headers = response.headers;

})(inputs, outputs);

